import React, { Component } from "react";
import './App.css';
import shuffle from './Shuffle'; // shuffle function from https://stackoverflow.com/a/2450976/1293256
import picArray from './Array'; // our array of image names and IDs

console.log("picArray:");
console.log(picArray);

var shuffledArray = shuffle(picArray.slice());

console.log("shuffledArray:");
console.log(shuffledArray);

class App extends Component {

  catchAnswer = (id) => {
    console.log(id);
  };

  render() {
    return (
      <div className="App">
        <div id="header">
          <span>Logo Goes Here</span> <span>You guessed right/wrong!</span> <span>Score: 12 | Top Score: 12</span>
        </div>
        <div id="second-header">
          <h1>Clicky Game</h1>
          <p>Click on an image to earn points, but don't click on any more than once!</p>
        </div>
        <div id="gameBody">
          {
            shuffledArray.map(item => (
              <img src={"./images/" + item[1]} alt="Redshirt" width="100" id={item[0]} class="guess" onClick={() => this.catchAnswer(item[0])}/>
            ))
          }
        </div>
        <div id="footer">
          <p>Some footer stuff</p>
        </div>
      </div>
    );
  }
}

export default App;
